catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var endDate = g_form.getValue('end_date');
    var ajax = new GlideAjax('x_snc_ow_credit_0.CostApprovalUtils');
    ajax.addParam('sysparm_name', 'startDate');
    ajax.addParam('sysparm_date', newValue);
    ajax.addParam('sysparm_end', endDate);
    ajax.getXML(dateValidation);
    var start_date = newValue.split(' ');

    function dateValidation(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        answer = JSON.parse(answer);
        if (answer.isStartpast) {
            g_form.clearValue('start_date');
            g_form.showFieldMsg('start_date', answer.msg, 'error');
        } else if (answer.isEndpast == true) {
            g_form.clearValue('start_date');
            g_form.showFieldMsg('start_date', answer.msg3, 'error');
        } else {
            g_form.hideFieldMsg('start_date');
        }
        if (answer.budgetRecord == false) {
            g_form.clearValue('start_date');
            g_form.showFieldMsg('start_date', answer.msg2, 'error');
        } else {
            g_form.setValue("budget", answer.budgetRecord);
        }
    }
}
